n = 1000;r0 = 3.5;r1 = 3.88;steps = 100;dotsize = 2;rs = [];xe = [];ye = [];for r=r0:(r1-r0)/steps:r1   x = logistic_map(r, 0.6, n);  y = logistic_map(r, 0.70, n);  rs = [rs, r];   xe = [xe, x(n)];  ye = [ye, y(n)];    subplot (2, 2, 1);  plot(1:n+1,x,'b--o','linestyle', 'none', 'marker', ".", 'markersize', dotsize, ...       1:n+1,y,'r--x','linestyle', 'none', 'marker', ".", 'markersize', dotsize);  axis([0 n 0 1]);  subplot (2, 2, 2);  plot(1:n+1,x-y,'g--o','linestyle', 'none', 'marker', ".", 'markersize', dotsize);  axis([0 n -1 1]);  title(["R:" num2str(r)]);  subplot (2, 2, 3);  plot(rs, xe, 'b---',rs, ye,'r---');  axis([r0 r1 0 1]);  sleep (0.01);end